import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ".styles/dashboard.css"; // Add your CSS here

function Dashboard({ username, setUsername }) {
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState("");
  const navigate = useNavigate();

  // Redirect if no username
  useEffect(() => {
    if (!username) navigate("/");
  }, [username, navigate]);

  // Load tasks from backend
  const loadTasks = async () => {
    try {
      const res = await fetch(`http://localhost:5000/tasks/${username}`);
      const data = await res.json();
      setTasks(data); // adjust if backend returns { tasks: [...] }
    } catch (err) {
      console.error("Error loading tasks:", err);
    }
  };

  useEffect(() => {
    loadTasks();
  }, [username]);

  // Add new task
  const addTask = async () => {
    if (!taskTitle.trim()) return;

    const newTask = {
      title: taskTitle,
      priority: "Medium",
      status: "Pending",
      created: new Date().toISOString().slice(0, 10),
      details: "",
    };

    await fetch(`http://localhost:5000/tasks/${username}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTask),
    });

    setTaskTitle("");
    loadTasks();
  };

  // Delete task
  const deleteTask = async (id) => {
    await fetch(`http://localhost:5000/tasks/${username}/${id}`, {
      method: "DELETE",
    });
    loadTasks();
  };

  // Logout
  const logout = () => {
    localStorage.removeItem("username");
    setUsername(null);
    navigate("/");
  };

  return (
    <div className="dashboard-wrapper">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>My App</h3>
        <button onClick={logout}>Logout</button>
      </div>

      {/* Main content */}
      <div className="main-content">
        {/* Topbar */}
        <div className="topbar">
          <h2>Welcome, {username}</h2>
        </div>

        {/* Task Input */}
        <div className="task-input">
          <input
            placeholder="New task title..."
            value={taskTitle}
            onChange={(e) => setTaskTitle(e.target.value)}
          />
          <button onClick={addTask}>Add Task</button>
        </div>

        {/* Task List */}
        <div className="task-list">
          {tasks.length === 0 ? (
            <p>No tasks yet. Add your first task!</p>
          ) : (
            tasks.map((t) => (
              <div className="task-card" key={t.id}>
                <h4>{t.title}</h4>
                <p>Status: {t.status}</p>
                <p>Priority: {t.priority}</p>
                <p>Created: {t.created}</p>
                <div className="task-actions">
                  <button onClick={() => deleteTask(t.id)}>Delete</button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
