import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/sidebar";
import Topbar from "../components/topbar";



function Dashboard({ username, setUsername }) {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState("");

  // Redirect if not logged in
  useEffect(() => {
    if (!username) navigate("/");
  }, [username, navigate]);

  

  function Dashboard({ username, setUsername }) {
    const [tasks, setTasks] = useState([]);
    const [taskTitle, setTaskTitle] = useState("");
    const navigate = useNavigate();

    if (!username) navigate("/");

    // ✅ Reusable loadTasks function
   

    // ✅ Call loadTasks when username changes
    useEffect(() => {
      if (username) loadTasks();
    }, [username, loadTasks]);

    // Add, delete tasks (keep your existing functions)
    const addTask = async () => { /* your code */ };
    const deleteTask = async (id) => { /* your code */ };

    // Logout example
    const logout = () => {
      localStorage.removeItem("username");
      setUsername(null); // make sure setUsername is passed from App.js
      navigate("/");
    };

    return (
      <div className="dashboard">
        {/* your JSX */}
      </div>
    );
  }

  // Add new task
  const addTask = async () => {
    if (!taskTitle.trim()) return;
    const newTask = {
      title: taskTitle,
      priority: "Medium",
      status: "Pending",
      created: new Date().toISOString().slice(0, 10),
      details: "",
    };

    await fetch(`http://localhost:5000/tasks/${username}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTask),
    });

    setTaskTitle("");
    loadTasks();
  };

  // Delete task
  const deleteTask = async (id) => {
    await fetch(`http://localhost:5000/tasks/${username}/${id}`, {
      method: "DELETE",
    });
    loadTasks();
  };

  // Logout
  const logout = () => {
    localStorage.removeItem("username");
    setUsername(null);
    navigate("/");
  };

  return (
    <div className="dashboard-wrapper">
      <Sidebar logout={logout} />
      <div className="main-content">
        <Topbar username={username} />

        <div className="dashboard-content">
          <h2>Welcome, {username}</h2>

          <div className="add-task">
            <input
              type="text"
              placeholder="New Task"
              value={taskTitle}
              onChange={(e) => setTaskTitle(e.target.value)}
            />
            <button onClick={addTask}>Add Task</button>
          </div>

          <div className="task-list">
            {tasks.length === 0 ? (
              <p>No tasks yet.</p>
            ) : (
              tasks.map((t) => (
                <div className="task-card" key={t.id}>
                  <h4>{t.title}</h4>
                  <p>Status: {t.status}</p>
                  <p>Priority: {t.priority}</p>
                  <div className="task-actions">
                    <button onClick={() => deleteTask(t.id)}>Delete</button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
