import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/dashboard.css";
import "../"


function Dashboard({ username }) {
  const [tasks, setTasks] = useState([]);          // define tasks
  const [taskTitle, setTaskTitle] = useState("");  // define taskTitle
  const navigate = useNavigate();                  // define navigate

  // Redirect if username is not present
  useEffect(() => {
    if (!username) {
      navigate("/");
    }
  }, [username, navigate]);

  // Load user tasks
  const loadTasks = async () => {
    if (!username) return;
    try {
      const res = await fetch(`http://localhost:5000/tasks/${username}`);
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      console.error("Error loading tasks:", err);
    }
  };

  useEffect(() => {
    loadTasks();
  }, [username]);

  // Add new task
  const addTask = async () => {
    if (!taskTitle.trim()) return;

    const newTask = {
      title: taskTitle,
      priority: "Medium",
      status: "Pending",
      created: new Date().toISOString().slice(0, 10),
      details: "",
    };

    try {
      await fetch(`http://localhost:5000/tasks/${username}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newTask),
      });
      setTaskTitle("");
      loadTasks();
    } catch (err) {
      console.error("Error adding task:", err);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      await fetch(`http://localhost:5000/tasks/${username}/${id}`, {
        method: "DELETE",
      });
      loadTasks();
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  return (
    <div className="dashboard">
      <h2>Welcome {username}</h2>

      <h3>Add Task</h3>
      <input
        placeholder="Task title"
        value={taskTitle}
        onChange={(e) => setTaskTitle(e.target.value)}
      />
      <button onClick={addTask}>Add Task</button>

      <h3 style={{ marginTop: "20px" }}>Your Tasks</h3>

      {tasks.length === 0 && <p>No tasks yet. Add your first task!</p>}

      {tasks.map((t) => (
        <div className="task-card" key={t.id}>
          <h4>{t.title}</h4>
          <p>Status: {t.status}</p>
          <p>Priority: {t.priority}</p>

          <div className="task-actions">
            <button onClick={() => deleteTask(t.id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Dashboard;
