// src/pages/Dashboard.js
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/dashboard.css";
import Sidebar from "../components/sidebar";
import Topbar from "../components/topbar";

// inside Dashboard component's return
<div className="dashboard-container">
  <Sidebar tasksCount={tasks.length} onLogout={() => {
    localStorage.removeItem("username");
    navigate("/");
  }} />
  <div className="main-content">
    <Topbar username={username} />
    <div className="dashboard">
      {/* Your tasks and add-task UI here */}
    </div>
  </div>
</div>
function Dashboard({ username }) {
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState("");
  const navigate = useNavigate();

  // If not logged in, redirect
  useEffect(() => {
    if (!username) navigate("/");
  }, [username]);

  // Load tasks
  const loadTasks = async () => {
    try {
      const res = await fetch(`http://localhost:5000/tasks/${username}`);
      const data = await res.json();
      setTasks(data);
    } catch (error) {
      console.error("Failed to load tasks:", error);
    }
  };

  useEffect(() => {
    if (username) loadTasks();
  }, [username]);

  // Add task
  const addTask = async () => {
    if (!taskTitle.trim()) return;

    const newTask = {
      title: taskTitle,
      priority: "Medium",
      status: "Pending",
      created: new Date().toISOString().slice(0, 10),
      details: "",
    };

    await fetch(`http://localhost:5000/tasks/${username}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTask),
    });

    setTaskTitle("");
    loadTasks();
  };

  // Delete task
  const deleteTask = async (id) => {
    await fetch(`http://localhost:5000/tasks/${username}/${id}`, {
      method: "DELETE",
    });
    loadTasks();
  };

  return (
    <div className="dashboard">
      <h2>Welcome, {username}</h2>

      <div className="add-task">
        <input
          placeholder="Task title"
          value={taskTitle}
          onChange={(e) => setTaskTitle(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <h3>Your Tasks</h3>
      {tasks.length === 0 ? (
        <p>No tasks yet. Add your first task!</p>
      ) : (
        tasks.map((t) => (
          <div className="task-card" key={t.id}>
            <h4>{t.title}</h4>
            <p>Status: {t.status}</p>
            <p>Priority: {t.priority}</p>
            <div className="task-actions">
              <button onClick={() => deleteTask(t.id)}>Delete</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Dashboard;
